import React, { Component } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

class ReplyPostView extends Component {
  constructor(props) {
    super(props);
    this.state = {
      r_data: [],
      id: props.id,
      r_id: [],
      title: props.title,
    };
  }

  componentDidMount() {
    const id = this.state.id;
    axios
      .get(`http://localhost:8000/api/rpv/${id}`)
      .then((res) => {
        this.setState({ r_data: res.data });
      })
      .then(() => {
        const user = this.state.r_data.map((i) => {
          return i.user;
        });

        axios.get(`http://localhost:8000/api/uv/${user}`).then((res) => {
          this.setState({ r_id: res.data });
        });
      });
      console.log(this.state.id)
  }

  render() {
    const { r_data, r_id } = this.state;
    const u = r_data.map((i) => {
      return (
        <li key={i.id}>
          <h1>body: {i.reply}</h1> <br /> <h1>user: {i.user}</h1> <br />
          {i.date_added} <br />{" "}
          <Link
          to={{
            pathname: `/scrp/${this.state.id}/${this.state.title}/`,
            state: { getp: i.id },
          }}
        >
          Reply
        </Link>
        </li>
      );
    });

    return (
      <div>
        <ul>{u}</ul>
      </div>
    );
  }
}

export default ReplyPostView;
