  render() {
    const usr_d = this.state.usr_d;
    const usr_m = usr_d.map((items) => {
      return (
        <li key={items.user_id}>
          username: {items.username} <br /> <br />
          profile: {items.profile}
          <br /> <br />
          first name: {items.first_name} <br /> <br />
          last name: {items.last_name} <br /> <br />
          user id: {items.user_id} <br /> <br />
          email: {items.email} <br /> <br />
          password: {items.password}
        </li>
      );
    });
    const usr_m_1 = usr_d.map((items) => {
      return (
        <li Key={items.user_id}>
          <form onSubmit={this.editUserD}>
            <input name="username" value={this.state.username} onChange={this.handleChange} placeholder='change username'></input>
            <input name="lol"></input>
            <input name="lol"></input>
            <input name="lol"></input>
            <input name="lol"></input>
            <input name="lol"></input>
            <button type='submit'>change</button>
          </form>
        </li>
      );
    });
    return (
      <div className="form-popup" id="myForm">
        {this.state.f_c ? (
          <div className="form-container">
            <button className="open-button" onClick={this.openForm}>
              edit User
            </button>
          </div>
        ) : (
          <div className="form-container">
            <ul>
              {usr_m} 
              {usr_m_1}
            </ul>
            <button
              type="button"
              className="btn cancel"
              onClick={this.closeForm}
            >
              Close
            </button>
          </div>
        )}
      </div>
    );
  }
}

export default EditUser;
