import React, {Component} from 'react';
import axios from 'axios';

class PostView extends Component {
    constructor(props) {
        super(props);
        this.state = {
         p_data: [],
         url_id: ''
        }
    }


componentDidMount() {
    const {url_id, p_data } = this.state;
    axios.get('http://localhost:8000/api/upv/')
    .then(res => {
        this.setState({ p_data : res.data})
        const id = this.props.match.params.id
        console.log(id)
        this.setState({ url_id : id})
    })

}

    render() {
        const id = this.props.match.params.id
        const {p_data} = this.state;

        const title = p_data.map(items => {
            return items.id
        })
        return (
            <div>
                {id ? <div>{title}</div> : <h1>Nope</h1>}
            </div>
        )
    }
}


export default PostView;
