import React, { Component } from "react";
import axios from "axios";

class PostView extends Component {
  constructor(props) {
    super(props);
    this.state = {
      p_data: [],
      url_id: "",
      p_d_id: [],
      title: ''
    };
  }

  componentDidMount() {
    const url_id = this.state.url_id;
    axios
      .get("http://localhost:8000/api/upv/")
      .then((res) => {
        this.setState({ p_data: res.data });
        const id = this.props.match.params.id;
        const title  = this.props.match.params.title;
        this.setState({ url_id: id, title: title });
      })
      .catch((err) => {
        console.log(err);
      });
    axios.get(`http://localhost:8000/api/upv/${url_id}`).then((res) => {
      this.setState({ p_d_id: res.data });
    });
  }

  render() {
    const { p_d_id, url_id, title } = this.state;
    const usr = p_d_id.find((items) => {
      if (items.id === url_id || items.title === title) {
        return (
          <div>
            <h1>title {items.title}</h1>
            <h1>body {items.body}</h1>
          </div>
        )
      } else {
        return //none
      }
    });
    return <div>{url_id ? <div>{usr}</div> : <h1>Nope</h1>}</div>;
  }
}

export default PostView;
