import { Component } from "react";
import "../styles/edituser.css";
import Cookies from "js-cookie";

class EditUser extends Component {
  constructor(props) {
    super(props);
    this.state = {
      f_c: true,
      usr_d: [],
    };
    this.openForm = this.openForm.bind(this);
    this.closeForm = this.closeForm.bind(this);
  }

  openForm() {
    this.setState({ f_c: false });
  }
  closeForm() {
    this.setState({ f_c: true });
  }

  componentDidMount() {
    const profile = Cookies.get("profile");
    const first_name = Cookies.get("first_name");
    const last_name = Cookies.get("last_name");
    const user_id = Cookies.get("user_id");
    const username = Cookies.get("username");
    const email = Cookies.get("email");
    const password = Cookies.get("password");

    this.setState({
      usr_d: profile,
      first_name,
      last_name,
      user_id,
      username,
      email,
      password,
    });
  }

  render() {
    const usr_d = this.state.usr_d;
    const usr_m = usr_d.map((items) => {
      <li Key={items.user_id}>
        profile: {items.profile} user_id: {items.user_id} first_name:{" "}
        {items.first_name} last_name: {items.last_name} email: {items.email}{" "}
        password: {items.password}
      </li>;
    });

    return (
      <div className="form-popup" id="myForm">
        {this.state.f_c ? (
          <div>
            <button className="open-button" onClick={this.openForm}>
              Open Form
            </button>
          </div>
        ) : (
          <div>
            <ul>{usr_m}</ul>
            <button
              type="button"
              className="btn cancel"
              onClick={this.closeForm}
            >
              Close
            </button>
          </div>
        )}
      </div>
    );
  }
}

export default EditUser;
