import React, {Component} from 'react';
import axios from 'axios';

class PostView extends Component {
    constructor(props) {
        super(props);
        this.state = {
         p_data: [],
         url_id: '',
         p_d_id: []
        }
    }


componentDidMount() {
    const {url_id, p_data } = this.state;
    axios.get('http://localhost:8000/api/upv/')
    .then(res => {
        this.setState({ p_data : res.data})
        const id = this.props.match.params.id
        this.setState({ url_id : id})
    })
    .then(() => {
        const {url_id, p_d_id } = this.state
        axios.get(`http://localhost:8000/api/upv/${url_id}`)
        .then(res => {
            this.setState({ p_d_id : res.data})
        })
    })

}

    render() {
        const {p_d_id, url_id} = this.state;
        const usr = p_d_id.map(items => {
            return <div>
                <h1>title {items.title}</h1>
                <h1>body {items.body}</h1>
            </div>
        })
        return (
            <div>
                {url_id ? <div>{usr}</div> : <h1>Nope</h1>}
            </div>
        )
    }
}


export default PostView;
