import { Component } from "react";
import "../styles/edituser.css";
import Cookies from "js-cookie";

class EditUser extends Component {
  constructor(props) {
    super(props);
    this.state = {
      f_c: true,
      usr_d: [Cookies.get()],
    };
    this.openForm = this.openForm.bind(this);
    this.closeForm = this.closeForm.bind(this);
  }

  openForm() {
    this.setState({ f_c: false });
  }
  closeForm() {
    this.setState({ f_c: true });
  }

  render() {
    const usr_d = this.state.usr_d;
    const usr_m = usr_d.map((items) => {
      return (
        <li key={items.user_id}>
          username: {items.username} <br /> <br/>
          profile: {items.profile}
          <br />
          first name: {items.first_name} <br />
          last name: {items.last_name} <br />
          user id: {items.user_id} <br />
          email: {items.email} <br />
          password: {items.password}
        </li>
      );
    });
    return (
      <div className="form-popup" id="myForm">
        {this.state.f_c ? (
          <div className="form-container">
            <button className="open-button" onClick={this.openForm}>
              edit User
            </button>
          </div>
        ) : (
          <div className="form-container">
            <ul>{usr_m}</ul>
            <button
              type="button"
              className="btn cancel"
              onClick={this.closeForm}
            >
              Close
            </button>
          </div>
        )}
      </div>
    );
  }
}

export default EditUser;
