import React, { Component } from "react";
import axios from "axios";

class ReplyPostView extends Component {
  constructor(props) {
    super(props);
    this.state = {
      r_data: [],
      id: props.id,
      r_id: '',
    };
  }

  componentDidMount() {
    const id = this.state.id;
    axios.get(`http://localhost:8000/api/rpv/${id}`).then((res) => {
      this.setState({ r_data: res.data });
      this.setState({ r_id : res.data.user})
    });
  }

  render() {
    const { r_data } = this.state;
    const u = r_data.map((i) => {
      return (
        <li key={i.id}>
          <h1>username: {i.username}</h1> <br /> <h1>body: {i.reply}</h1> <br />
          {i.date_added}
        </li>
      );
    });
    return (
      <div>
        <ul>{u}</ul>
      </div>
    );
  }
}

export default ReplyPostView;
