import React, {Component} from 'react';
import axios from 'axios';

class PostView extends Component {
    constructor(props) {
        super(props);
        this.state = {
         p_data: [],
         url_id: ''
        }
    }


componentDidMount() {
    const {url_id, p_data } = this.state;
    axios.get('http://localhost:8000/api/upv/')
    .then(res => {
        this.setState({ p_data : res.data})
        const id = this.props.location.query.id
        console.log(id)
        this.setState({ url_id : id})
    })

}

    render() {
        const queryString = window.location.search;
        const urlParams = new URLSearchParams(queryString)
        const id = urlParams.get('id')
        const usr = this.state.p_data.map(items => {
            return (
                <div>
                    <h1>title: {items.title}</h1>
                    <br />
                    <p>body: {items.body}</p>
                </div>
            )
        })
        return (
            <div>
                {id ? <div>{usr}</div> : <h1>Nope</h1>}
            </div>
        )
    }
}


export default PostView;
