import React, { Component } from "react";
import axios from "axios";

class PostView extends Component {
  constructor(props) {
    super(props);
    const url_id = this.props.match.params.id;
    this.state = {
      p_data: [],
      p_d_id: [],
      title: "",
    };
  }

  componentDidMount() {
    const url_id = this.state.url_id;
    axios
      .get("http://localhost:8000/api/upv/")
      .then((res) => {
        this.setState({ p_data: res.data });
        const title = this.props.match.params.title;
      })
      .catch((err) => {
        console.log(err);
      });
    axios.get(`http://localhost:8000/api/upv/${url_id}`).then((res) => {
      this.setState({ p_d_id: res.data });
    });
  }

  render() {
    let p_d_id = this.url_id;
    const url_id = this.state.url_id;

    const id = p_d_id.map((item) => {
      return item.id;
    });

    const usr = p_d_id.find(function (item) {
      if (url_id === id) {
        return <h1>Hello</h1>;
      }
    });

    return <div>{url_id ? <div>{usr}</div> : <h1>Nope</h1>}</div>;
  }
}

export default PostView;
