import React, { Component } from "react";
import axios from "axios";

class ReplyPostView extends Component {
  constructor(props) {
    super(props);
    this.state = {
      r_data: [],
      id: props.id,
      r_id: [],
    };
  }

  componentDidMount() {
    const id = this.state.id;
    axios
      .get(`http://localhost:8000/api/rpv/${id}`)
      .then((res) => {
        this.setState({ r_data: res.data });
      })
      .then(() => {
        const user = this.state.r_data.map((i) => {
          return i.user;
        });

        axios.get(`http://localhost:8000/api/uv/${user}`).then((res) => {
          this.setState({ r_id: res.data });
        });
      });
  }

  render() {
    const { r_data, r_id } = this.state;
    const a = r_id.map((i) => {
      return i.username;
    });
    const u = r_data.map((i) => {
      return (
        <li key={i.id}>
          <h1>body: {i.reply}</h1> <br /> <h1>username: {a}</h1> <br />
          {i.date_added}
        </li>
      );
    });

    return (
      <div>
        <ul>{u}</ul>
      </div>
    );
  }
}

export default ReplyPostView;
