import { Component } from "react";
import "../styles/edituser.css";
import Cookies from "js-cookie";

class EditUser extends Component {
  constructor(props) {
    super(props);
    this.state = {
      f_c: true,
      usr_d: [Cookies.get()],
    };
    this.openForm = this.openForm.bind(this);
    this.closeForm = this.closeForm.bind(this);
  }

  openForm() {
    this.setState({ f_c: false });
  }
  closeForm() {
    this.setState({ f_c: true });
  }

  render() {
    const usr_d = this.state.usr_d;
    const usr_m = usr_d.map((items) => {
     return <li key={items.user_id}>username: {items.username}</li>;
    });
    return (
      <div className="form-popup" id="myForm">
        {this.state.f_c ? (
          <div>
            <button className="open-button" onClick={this.openForm}>
              Open Form
            </button>
          </div>
        ) : (
          <div>
            <ul>{usr_m}</ul>
            <button
              type="button"
              className="cancel"
              onClick={this.closeForm}
            >
              Close
            </button>
          </div>
        )}
      </div>
    );
  }
}

export default EditUser;
