import React, { Component } from "react";
import axios from "axios";
import "../../styles/prv.css";

class ReplyPostView extends Component {
  constructor(props) {
    super(props);
    this.state = {
      r_data: [],
      id: props.id,
      r_id: [],
      title: props.title,
    };
  }

  componentDidMount() {
    const id = this.state.id;

    axios.get(`http://localhost:8000/api/rpv/${id}`).then((res) => {
    this.setState({ r_data: res.data });
    });
  }

  render() {
    const { r_data } = this.state;
    const u = r_data.map((i) => {
      return (
        <div className="prv-list">
        <li key={i.id}>
          <h1 className="prv-h1-body">{i.reply}</h1> <br />
          <h1 className="prv-h1-date">
            {i.date_added} <br />
          </h1>
        </li>
        <div>
        <textarea className="prv-h1-user">user: {i.user}</textarea> <br />
        </div>
        </div>
      );
    });

    return (
      <div>
        <ul>{u}</ul>
      </div>
    );
  }
}

export default ReplyPostView;
