import React, { Component } from "react";
import axios from "axios";

class PostView extends Component {
  constructor(props) {
    super(props);
    this.state = {
      p_data: [],
      url_id: "",
      p_d_id: [{}],
      title: "",
    };
  }

  componentDidMount() {
    const url_id = this.state.url_id;
    axios
      .get("http://localhost:8000/api/upv/")
      .then((res) => {
        this.setState({ p_data: res.data });
        const id = this.props.match.params.id;
        const title = this.props.match.params.title;
        this.setState({ url_id: id, title: title });
      })
      .catch((err) => {
        console.log(err);
      });
    axios.get(`http://localhost:8000/api/upv/${url_id}`).then((res) => {
      this.setState({ p_d_id: res.data });
    });
  }

  render() {
    let p_d_id = this.state.p_d_id;
    const url_id = this.state.url_id;
    const u = () => {
      for (id in p_d_id) {
        if (id === url_id) {
          return p_d_id.map(i => {
            return <h1>{i.title}</h1>
          })
        }
      }
    }
    console.log(url_id)
    return (
      <div>
        {url_id ? (
          <div>
            <h1>title {u}</h1>
          </div>
        ) : (
          <h1>Nope</h1>
        )}
      </div>
    );
  }
}

export default PostView;
