import React, { Component } from "react";
import axios from "axios";

class ReplyPostView extends Component {
  constructor(props) {
    super(props);
    this.state = {
      r_data: [],
      id: props.id,
    };
  }

  componentDidMount() {
    const id = this.state.id;
    axios.get(`http://localhost:8000/api/rpv/${id}`).then((res) => {
      this.setState({ r_data: res.data });
    });
  }

  render() {
    const { r_data } = this.state;
    const u = r_data.map((i) => {
      return (
        <li key={i.id}>
          username: {i.username} <br /> body: {i.reply} <br /> date: {i.date}
        </li>
      );
    });
    return (
      <div>
        <ul>
          {u}
        </ul>
      </div>
    );
  }
}

export default ReplyPostView;
